INSERT INTO `blog_translations` (`id`, `blog_id`, `title`, `short_desc`, `tags`, `slug`, `description`, `meta_title`, `meta_desc`, `meta_keywords`, `locale`) VALUES (6,8,'هبوط اليورو بسبب تراجع معنويات قطاع الأعمال الألمانيه','لندن (رويترز) - هبط اليورو يوم الجمعة بعدما أظهر مسح أن معنويات قطاع الأعمال الألماني تراجعت للشهر السادس على التوالي، بينما تعافى الدولار الأسترالي بعد أن نفت الصين حظر استيراد الفحم من أستراليا.',NULL,NULL,'<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong><span style=\"color:#000099\"><em><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">هبوط اليورو بسبب تراجع معنويات قطاع الأعمال الألمانيه</span></span></em></span></strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">لندن (رويترز) - هبط اليورو يوم الجمعة بعدما أظهر مسح أن معنويات قطاع الأعمال الألماني تراجعت للشهر السادس على التوالي، بينما تعافى الدولار الأسترالي بعد أن نفت الصين حظر استيراد الفحم من أستراليا</span>.</span></strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">وكان للبيانات السيئة منذ يناير دعم اليورو، الذي انخفض لكنه ظل رابحا مقابل العملة الأمريكية عند 1.1351 دولار. وكانت العملة الأوروبية الموحدة قد سجلت أعلى مستوى في أسبوعين عند 1.1371 دولار يوم الأربعاء بدعم من آمال انحسار النزاع التجاري بين الولايات المتحدة والصين</span>.</span></strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">وارتفع الدولار الأسترالي إلى أعلى مستوى خلال الجلسة عند 0.7117 دولار أمريكي، بزيادة 0.3 بالمئة</span>.</span></strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">واستقر الدولار الأمريكي مقابل سلة عملات كبرى</span></span></strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>','هبوط اليورو بسبب تراجع معنويات قطاع الأعمال الألمانيه','لندن (رويترز) - هبط اليورو يوم الجمعة بعدما أظهر مسح أن معنويات قطاع الأعمال الألماني تراجعت للشهر السادس على التوالي، بينما تعافى الدولار الأسترالي بعد أن نفت الصين حظر استيراد الفحم من أستراليا.',NULL,'ar'),(7,9,'مجلس الاحتياطي الفيدرالي: الاقتصاد الأمريكى يختم عام 2018 بنمو قوى','قال مجلس الاحتياطى الفيدرالي، يوم الجمعة الماضية ، فى تقريره نصف السنوى إلى الكونجرس بشأن السياسة النقدية إن الاقتصاد الأمريكى حافظ على نمو \"قوى\" فى النصف الثانى من 2018، منهيا العام على الأرجح على نمو',NULL,NULL,'<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>قال مجلس الاحتياطى الفيدرالي، يوم الجمعة الماضية ، فى تقريره نصف السنوى إلى الكونجرس بشأن السياسة النقدية إن الاقتصاد الأمريكى حافظ على نمو &quot;قوى&quot; فى النصف الثانى من 2018، منهيا العام على الأرجح على نمو يقل قليلا عن 3% رغم أن إنفاق المستهلكين والشركات بدأ يضعف.</strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>وفى وثيقة حققت توازنا بين توقعاته الأكثر إيجابية لاستمرار النمو الاقتصادى مقابل مجموعة من المخاطر المحلية والعالمية الناشئة، شرح البنك المركزى الأمريكى السبب وراء تعليق زيادات أخرى فى أسعار الفائدة الشهر الماضى.</strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>وقال مجلس الاحتياطى&nbsp; ، إن عوامل من بينها &quot;تدهور&quot; فى الإقبال على المخاطرة بين المستثمرين وتباطؤ فى الصين، جعلت توقعات السياسة النقدية &quot;أكثر ضبابية مما مضى&quot;، مشيرا إلى &quot;أوضاع عالمية واقتصادية أكثر ضعفا&quot;.</strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>وقال المركزى الأمريكى ، إن ذلك ربما امتد إلى بداية 2019، مشيرا إلى أن الإغلاق الجزئى للحكومة الأمريكية فى الآونة الأخيرة والذى استمر 35 يوما &quot;كبح على الأرجح نمو الناتج المحلى الإجمالى فى الربع الأول من هذا العام&quot;.</strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>وعن عام 2018، قال مجلس الاحتياطى &quot;إنفاق المستهلكين زاد بأقوى وتيرة فى معظم النصف الثاني... على الرغم من أن الإنفاق يبدو أنه ضعف قرب نهاية العام&quot;. ، مضيفا إن &quot;إنفاق الشركات نما أيضا، على الرغم من أنه يبدو أنه تباطأ نوعا ما&quot;.</strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>وذكر المجلس ، أن ثقة المستهلكين والشركات تظل &quot;مواتية&quot;، لكنه قال إن &quot;بعض المعايير ضُعفت منذ الخريف... الأوضاع المالية المحلية للشركات والأسر أصبحت أقل دعما للنمو الاقتصادي&quot;.</strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>ومن المقرر أن يدلى جيروم باول ، رئيس مجلس الاحتياطى الاتحادى بشهادته أمام مشرعين من مجلسى الشيوخ والنواب يومى الثلاثاء والأربعاء لتوضيح التقرير ، مشيرا الى أن بعض القوة الاقتصادية الكامنة، مع &quot;تحسينات مستمرة فى سوق العمل&quot; ونمو قوى فى الدخل القابل للإنفاق، والذى غذته التخفيضات الضريبية التى استحدثتها إدارة ترامب، وهو ما يدعم استهلاك الأسر.</strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>وظل التضخم العام الماضى قريبا من مستوى اثنين بالمئة الذى يستهدفه المركزى الأمريكي ، لكن المجلس أشار إلى عوامل غير مواتية، بما فى ذلك تلك المرتبطة بالجدل المستمر بشأن سياسة التجارة العالمية. وقال إنه بشكل عام، فإن صافى الصادرات &quot;خصم على الأرجح القليل من نمو الناتج المحلى الإجمالى الحقيقي&quot; على مدار العام 2018، على الرغم من جهود الإدارة لتحسين الموقف التجارى الأمريكي.</strong></span></p>','مجلس الاحتياطي الفيدرالي: الاقتصاد الأمريكى يختم عام 2018 بنمو قوى','قال مجلس الاحتياطى الفيدرالي، يوم الجمعة الماضية ، فى تقريره نصف السنوى إلى الكونجرس بشأن السياسة النقدية إن الاقتصاد الأمريكى حافظ على نمو \"قوى\" فى النصف الثانى من 2018، منهيا العام على الأرجح على نمو',NULL,'ar'),(9,11,'محافظ البنك المركزي الصيني توقف التدخل في سوق النقد الأجنبي','قال محافظ البنك المركزي الصيني يوم الأحد إن البنك توقف عن التدخل في سوق النقد الأجنبي وإن سعر صرف اليوان ليس مبعث قلق أساسي عند وضع السياسة النقدية..',NULL,NULL,'<p style=\"text-align:center\"><span style=\"font-size:18px\">بكين (رويترز) - قال محافظ البنك المركزي الصيني يوم الأحد إن البنك توقف عن التدخل في سوق النقد الأجنبي وإن سعر صرف <a href=\"https://sa.investing.com/currencies/usd-cny\">اليوان</a> ليس مبعث قلق أساسي عند وضع السياسة النقدية.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\">وقال يي قانغ محافظ بنك الشعب الصيني في مؤتمر صحفي على هامش الاجتماع السنوي للبرلمان إن الصين لن تستغل أسعار الصرف لتعزيز الصادرات ولا كأداة في الخلافات التجارية.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\">وأضاف يي أن الصين ستبقي اليوان مستقرا وستواصل الانفتاح المالي بكل تصميم وفقا لجدولها الزمني ومتطلبات الإصلاح.</span></p>','محافظ البنك المركزي الصيني توقف التدخل في سوق النقد الأجنبي','قال محافظ البنك المركزي الصيني يوم الأحد إن البنك توقف عن التدخل في سوق النقد الأجنبي وإن سعر صرف اليوان ليس مبعث قلق أساسي عند وضع السياسة النقدية..',NULL,'ar'),(12,14,'الدولار ين يرتفع أعلى مستويات 111.00','تلقى زوج الدولار ين بعض طلبات الشراء دون مستويات 111.00 وذلك بعد التراجع الذي شهده في وقت سابق متأثرًا بسلبية بيانات التوظيف الأمريكية التي ظهرت يوم الجمعة.',NULL,NULL,'<p style=\"text-align:center\"><span style=\"font-size:14px\"><strong><span style=\"color:#c0392b\">&nbsp;الدولار ين يرتفع أعلى مستويات 111.00</span></strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><strong>تلقى زوج الدولار ين بعض طلبات الشراء دون مستويات 111.00 وذلك بعد التراجع الذي شهده في وقت سابق متأثرًا بسلبية بيانات التوظيف الأمريكية التي ظهرت يوم الجمعة.</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<span style=\"font-size:14px\"><strong>وعلي هذا نجح زوج الدولار ين في الارتداد بحوالي 35 نقطة أساسية من أدنى مستويات سجلها خلال الفترة الآسيوية ليتداول حاليًا قرابة النطاق 111.20</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<span style=\"font-size:14px\"><strong>وأيضا فقد أضاف الاقتصاد الأمريكي 20 ألف وظيفة فقط خلال فبراير وهو ما أثار المخاوف بشأن أداء الاقتصاد الأمريكي وكان لهذه البيانات مردود سلبي على أداء الدولار الأمريكي.</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><strong>هذا وتترقب الأسواق بوقت لاحق من اليوم بيانات مبيعات التجزئة الأمريكية والمتوقع أن يكون لها تأثير مباشر على تداولات الدولار والعملات الرئيسية بشكل مجمل.</strong></span></p>','الدولار ين يرتفع أعلى مستويات 111.00','تلقى زوج الدولار ين بعض طلبات الشراء دون مستويات 111.00 وذلك بعد التراجع الذي شهده في وقت سابق متأثرًا بسلبية بيانات التوظيف الأمريكية التي ظهرت يوم الجمعة.',NULL,'ar'),(13,15,'اليورو دولار يرتفع مرة أخري خلال تداولات اليوم قرابة 1.1240','شهد الدولار الأمريكي ضعفا  متأثرا بسلبية بيانات التوظيف التي ظهرت يوم الجمعة الماضي مما أدي الي ارتفاع الزوج..',NULL,NULL,'<p style=\"text-align:center\"><strong>اليورو دولار يرتفع مرة أخري خلال تداولات اليوم قرابة 1.1240</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-size:14px\">شهد الدولار الأمريكي ضعفا&nbsp; متأثرا بسلبية بيانات التوظيف التي ظهرت يوم الجمعة الماضي مما أدي الي ارتفاع الزوج</span></strong></p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<strong><span style=\"font-size:14px\">والذي أدي الي ارتداد الزوج من أدني مستويات سجلها في عام 2019 بسبب<br />\r\nسلبية اجتماع المركزي الأوربي الذي عقد يوم الخميس الماضي</span></strong></p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<strong><span style=\"font-size:14px\">والجدير بالذكر ارتفع الزوج بالرغم من سلبية البيانات الألمانية التي ظهرت صباح اليوم وأظهرت انكماش الإنتاج الصناعي الألماني على أساس شهري بنسبة 0.8% في يناير في حين أن الفائض التجاري انكمش ليصل إلى 18.5 مليار يورو في الفترة ذاتها.</span></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-size:14px\">هذا وتترقب الأسواق بوقت لاحق من اليوم بيانات مبيعات التجزئة الأمريكية والمتوقع أن يكون لها تأثير مباشر على تداولات الدولار والعملات الرئيسية بشكل مجمل.</span></strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>','اليورو دولار يرتفع مرة أخري خلال تداولات اليوم قرابة 1.1240','شهد الدولار الأمريكي ضعفا  متأثرا بسلبية بيانات التوظيف التي ظهرت يوم الجمعة الماضي مما أدي الي ارتفاع الزوج..',NULL,'ar'),(15,17,'مستويات الطلب لزوج النيوزيلندي دولار والسيناريو المتوقع','الزوج بعد الارتداد من مستويات الطلب علي فريم الأربع ساعات عند مستويات 0.6750\r\nنلاحظ بداية ظهور برايس أكشن ايجابي يدعم استمرار الصعود لمستويات 0.6940',NULL,NULL,'<p style=\"text-align:center\"><strong><span style=\"font-size:14px\"><em>مستويات الطلب لزوج النيوزيلندي دولار والسيناريو المتوقع</em></span></strong></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"/ckfinder/userfiles/files/Ashampoo_Snap_11%20%D9%85%D8%A7%D8%B1%D8%B3%2C%202019_13h05m09s_006_.png\" style=\"height:645px; width:1416